import React, { useState } from 'react';
import {
  Container,
  Paper,
  TextField,
  Button,
  Typography,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  Grid,
  Box,
  Tabs,
  Tab
} from '@material-ui/core';
import axios from 'axios';

function App() {
  // Estados principales
  const [paso, setPaso] = useState(0); // 0: Configuración, 1: Detalles, 2: Resultados
  const [objetivo, setObjetivo] = useState('max');
  const [variables, setVariables] = useState([{ nombre: 'X0', valor: '' }, { nombre: 'X1', valor: '' }]);
  const [restricciones, setRestricciones] = useState([]);
  const [nuevaRestriccion, setNuevaRestriccion] = useState({ x: '', y: '', operador: '<=', valor: '' });
  const [coeficientes, setCoeficientes] = useState({ x: '', y: '' });
  const [resultado, setResultado] = useState(null);

  // Navegación entre pasos
  const siguiente = () => setPaso((prev) => Math.min(prev + 1, 2));
  const volver = () => setPaso((prev) => Math.max(prev - 1, 0));

  // Agregar restricción
  const agregarRestriccion = () => {
    setRestricciones([...restricciones, nuevaRestriccion]);
    setNuevaRestriccion({ x: '', y: '', operador: '<=', valor: '' });
  };

  // Resolver el modelo
  const resolverProblema = async () => {
    try {
      const datos = {
        objetivo,
        coeficientes: [parseFloat(coeficientes.x), parseFloat(coeficientes.y)],
        restricciones: restricciones.map(r => [
          parseFloat(r.x),
          parseFloat(r.y),
          r.operador,
          parseFloat(r.valor)
        ])
      };
      const respuesta = await axios.post('http://localhost:5000/resolver', datos);
      setResultado(respuesta.data);
      siguiente();
    } catch (error) {
      alert('Error al resolver el problema');
    }
  };

  // Tabs para navegación visual
  const tabs = [
    "Configuración del Modelo",
    "Detalles del Modelo",
    "Presentación de los Resultados"
  ];

  return (
    <Container maxWidth="lg" style={{ marginTop: '2rem' }}>
      <Paper style={{ padding: '2rem' }}>
        <Tabs value={paso} indicatorColor="primary" textColor="primary" centered>
          {tabs.map((label, idx) => (
            <Tab key={idx} label={label} />
          ))}
        </Tabs>

        {/* Paso 1: Configuración */}
        {paso === 0 && (
          <Box mt={4}>
            <Typography variant="h5" gutterBottom>
              Comenzamos configurando nuestro modelo
            </Typography>
            <Grid container spacing={2}>
              <Grid item xs={6} md={3}>
                <Paper style={{ padding: '1rem', textAlign: 'center' }}>
                  <Typography>Programación entera</Typography>
                  <Button variant="outlined" color="secondary" disabled>Inactiva</Button>
                </Paper>
              </Grid>
              <Grid item xs={6} md={3}>
                <Paper style={{ padding: '1rem', textAlign: 'center' }}>
                  <Typography>Gestión de Modelos</Typography>
                  <Button variant="outlined" color="primary">Modelos</Button>
                </Paper>
              </Grid>
              <Grid item xs={6} md={3}>
                <Paper style={{ padding: '1rem', textAlign: 'center' }}>
                  <Typography>Método a utilizar</Typography>
                  <Button variant="contained" color="primary">Gráfico</Button>
                  <Button variant="outlined" style={{ marginLeft: 8 }}>Simplex</Button>
                </Paper>
              </Grid>
              <Grid item xs={6} md={3}>
                <Paper style={{ padding: '1rem', textAlign: 'center' }}>
                  <Typography>Tipo de optimización</Typography>
                  <Button
                    variant={objetivo === 'max' ? "contained" : "outlined"}
                    color="primary"
                    onClick={() => setObjetivo('max')}
                  >Maximizar</Button>
                  <Button
                    variant={objetivo === 'min' ? "contained" : "outlined"}
                    color="primary"
                    onClick={() => setObjetivo('min')}
                    style={{ marginLeft: 8 }}
                  >Minimizar</Button>
                </Paper>
              </Grid>
            </Grid>
            <Box mt={4}>
              <Typography variant="h6">Variables</Typography>
              <Grid container spacing={2}>
                {variables.map((v, idx) => (
                  <Grid item xs={6} md={3} key={idx}>
                    <TextField
                      label={v.nombre}
                      value={v.valor}
                      onChange={e => {
                        const newVars = [...variables];
                        newVars[idx].valor = e.target.value;
                        setVariables(newVars);
                        if (idx === 0) setCoeficientes(c => ({ ...c, x: e.target.value }));
                        if (idx === 1) setCoeficientes(c => ({ ...c, y: e.target.value }));
                      }}
                      fullWidth
                    />
                  </Grid>
                ))}
              </Grid>
            </Box>
            <Box mt={4}>
              <Typography variant="h6">Restricciones</Typography>
              <Grid container spacing={2}>
                {restricciones.map((r, idx) => (
                  <Grid item xs={12} key={idx} style={{ display: 'flex', alignItems: 'center' }}>
                    <TextField
                      value={`${r.x} X0 + ${r.y} X1 ${r.operador} ${r.valor}`}
                      fullWidth
                      disabled
                    />
                    <Button
                      variant="outlined"
                      color="secondary"
                      style={{ marginLeft: 8, minWidth: 40 }}
                      onClick={() => {
                        setRestricciones(restricciones.filter((_, i) => i !== idx));
                      }}
                    >
                      🗑️
                    </Button>
                  </Grid>
                ))}
                <Grid item xs={2}>
                  <TextField
                    label="Coef X0"
                    type="number"
                    value={nuevaRestriccion.x}
                    onChange={e => setNuevaRestriccion({ ...nuevaRestriccion, x: e.target.value })}
                    fullWidth
                  />
                </Grid>
                <Grid item xs={2}>
                  <TextField
                    label="Coef X1"
                    type="number"
                    value={nuevaRestriccion.y}
                    onChange={e => setNuevaRestriccion({ ...nuevaRestriccion, y: e.target.value })}
                    fullWidth
                  />
                </Grid>
                <Grid item xs={2}>
                  <FormControl fullWidth>
                    <Select
                      value={nuevaRestriccion.operador}
                      onChange={e => setNuevaRestriccion({ ...nuevaRestriccion, operador: e.target.value })}
                    >
                      <MenuItem value="<=">≤</MenuItem>
                      <MenuItem value=">=">≥</MenuItem>
                      <MenuItem value="=">=</MenuItem>
                    </Select>
                  </FormControl>
                </Grid>
                <Grid item xs={3}>
                  <TextField
                    label="Valor"
                    type="number"
                    value={nuevaRestriccion.valor}
                    onChange={e => setNuevaRestriccion({ ...nuevaRestriccion, valor: e.target.value })}
                    fullWidth
                  />
                </Grid>
                <Grid item xs={1}>
                  <Button
                    variant="contained"
                    color="primary"
                    onClick={agregarRestriccion}
                    fullWidth
                  >+</Button>
                </Grid>
              </Grid>
            </Box>
            <Box mt={4} display="flex" justifyContent="flex-end">
              <Button variant="contained" color="primary" onClick={siguiente}>Siguiente</Button>
            </Box>
          </Box>
        )}

        {/* Paso 2: Detalles del Modelo */}
        {paso === 1 && (
          <Box mt={4}>
            <Typography variant="h5" gutterBottom>
              Cargamos los datos de nuestro modelo
            </Typography>
            <Box mt={2}>
              <Typography variant="subtitle1">Referencias</Typography>
              <Paper style={{ padding: '1rem', marginBottom: '1rem' }}>
                <Typography>Variables:</Typography>
                {variables.map((v, idx) => (
                  <Typography key={idx}>{v.nombre}: {v.valor}</Typography>
                ))}
                <Typography style={{ marginTop: 8 }}>Restricciones:</Typography>
                {restricciones.map((r, idx) => (
                  <Typography key={idx}>
                    R{idx}: {r.x} X0 + {r.y} X1 {r.operador} {r.valor}
                  </Typography>
                ))}
              </Paper>
            </Box>
            <Box mt={2}>
              <Typography variant="subtitle1">Función objetivo</Typography>
              <Paper style={{ padding: '1rem', marginBottom: '1rem' }}>
                <Typography>
                  {coeficientes.x} X0 + {coeficientes.y} X1 {objetivo === 'max' ? '=> MAX' : '=> MIN'}
                </Typography>
              </Paper>
            </Box>
            <Box mt={2}>
              <Typography variant="subtitle1">Restricciones</Typography>
              <Paper style={{ padding: '1rem', marginBottom: '1rem' }}>
                {restricciones.map((r, idx) => (
                  <Typography key={idx}>
                    R{idx}: {r.x} X0 + {r.y} X1 {r.operador} {r.valor}
                  </Typography>
                ))}
              </Paper>
            </Box>
            <Box mt={4} display="flex" justifyContent="space-between">
              <Button variant="outlined" onClick={volver}>Volver</Button>
              <Button variant="contained" color="primary" onClick={resolverProblema}>Siguiente</Button>
            </Box>
          </Box>
        )}

        {/* Paso 3: Resultados */}
        {paso === 2 && resultado && (
          <Box mt={4}>
            <Typography variant="h5" gutterBottom>
              El resultado óptimo de la función objetivo es: {resultado.valor_objetivo}
            </Typography>
            <Box mt={2}>
              <Typography variant="subtitle1">Gráfico:</Typography>
              <img
                src={`data:image/png;base64,${resultado.grafico}`}
                alt="Gráfico de la solución"
                style={{ width: '400px', marginTop: '1rem', border: '1px solid #ccc' }}
              />
            </Box>
            <Box mt={2}>
              <Typography variant="subtitle1">Referencias</Typography>
              <Paper style={{ padding: '1rem', marginBottom: '1rem' }}>
                <Typography>Variables: {resultado.referencias?.variables?.join(', ')}</Typography>
                <Typography>Restricciones: {resultado.referencias?.restricciones?.join(', ')}</Typography>
              </Paper>
            </Box>
            <Box mt={2}>
              <Typography variant="subtitle1">Tabla de Puntos:</Typography>
              <table style={{ width: '100%', borderCollapse: 'collapse', marginTop: '1rem' }}>
                <thead>
                  <tr>
                    <th style={{ border: '1px solid #ccc', padding: '4px' }}>Punto</th>
                    <th style={{ border: '1px solid #ccc', padding: '4px' }}>X</th>
                    <th style={{ border: '1px solid #ccc', padding: '4px' }}>Y</th>
                    <th style={{ border: '1px solid #ccc', padding: '4px' }}>Valor Objetivo</th>
                  </tr>
                </thead>
                <tbody>
                  {resultado.tabla_resultados?.map((p, idx) => (
                    <tr key={idx} style={p.punto.includes('OPTIMO') ? { background: '#e0ffe0' } : {}}>
                      <td style={{ border: '1px solid #ccc', padding: '4px' }}>{p.punto}</td>
                      <td style={{ border: '1px solid #ccc', padding: '4px' }}>{p.x}</td>
                      <td style={{ border: '1px solid #ccc', padding: '4px' }}>{p.y}</td>
                      <td style={{ border: '1px solid #ccc', padding: '4px' }}>{p.valor_objetivo}</td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </Box>
            <Box mt={4} display="flex" justifyContent="flex-end">
              <Button variant="outlined" onClick={volver}>Volver</Button>
            </Box>
          </Box>
        )}
      </Paper>
    </Container>
  );
}

export default App;
